    private void jTextAreaDescKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_jTextAreaDescKeyPressed
        if (evt.getKeyCode() == VK_TAB) {
            evt.consume();
            jButtonSalvar.requestFocus();
        }
    }//GEN-LAST:event_jTextAreaDescKeyPressed

